import React, { useState } from "react";
import './NewExpense.css';
import ExpenseForm from "./NewForm";
import AddExpensesButton from "./AddNewExpensesButton";
const NewExpenses = (props) =>{
   const [newVar, setNewVar] =  useState(false);
    const saveUserDateFromNewFormHandler =(enteredExpenseData)=>{
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString(),
        }
        props.onAddExpense(expenseData);
    };
    // const newButtonAddExpense =(e)=>{
    //     e.preventDefault();
    //     console.log("click");
    // }


    return(
        <div className="new-expense">
            <AddExpensesButton onClick = {setNewVar(true)}/>
            {newVar === true && <ExpenseForm onRequerUserDateFromNewForm = {saveUserDateFromNewFormHandler}/>} 
            
        </div>
    )
}

export default NewExpenses;
